class game:
    
    def __init__(self):
        self.main_menu()

    def main_menu(self): # меню
        global balance 

        system('clear')
        otvet = input(f'Любимые слоты!\n\n\nВаш баланс равен {balance}!\nОдна игра стоит 100. Хотите начать игру или пополнить баланс? (game/bal/exit)')
        if otvet == 'game':
            system('clear')
            self.cazik()
        elif otvet =='exit':
            pass
        else:
            system('clear')
            self.balance_chose()

    def balance_chose(self): # выбор пополнения баланса
        global balance 

        print(f'На данный момент ваш баланс осотавляет: {balance}\nСпособоы пополнения баланса:\n1. Примеры - 200\n2. Слова - 100')
        chose = input('Введите номер выбора: ')
        if chose == '1':
            self.balance_plus_math()
        elif chose == '2':
            self.balance_plus_gram()

    def balance_plus_math(self): # пополнение математикой
        a_math = random.randint(0, 256)
        b_math = random.randint(0, 256)
        znak_list = ['//', '*', '+', '-']
        znak = random.choice(znak_list)
